package week2homeworkscodingchallengers;

public class PrimeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//prime numbers
		for(int i=2;i<=30;i++)
		{
			for(int k=2;k<=i;k++)
			{
				if(k==i)
				{
					System.out.println(i);
					{
						if(i%k==0)
						{
							break;

						}

					}
				}
			}
		}



	}

}


